\begin{tikzpicture} \begin{axis}[ every tick label/.append style={font=\small}, axis x line = middle, axis y line = middle, every axis y label/.style={at={(ticklabel cs:1.15)}}, %ytick = {-4, -2, -3, -1, 1, 2, 3, 4}, y label style={at={(axis description cs:.47,1.15)},anchor=north}, ylabel = {$f(x)$}, every axis x label/.style= {at ={(ticklabel cs:1)}}, %xtick = {-4,-3,-2,-1,1,2,3,4}, x label style={at={(axis description cs:1.1,0)},anchor=east}, xlabel = {$x$}, xmin = -1.5, xmax = 2 ] \addplot[<->,thick, samples = 100, smooth, domain = -1.4:1.9] {6*x^4-6*x^3-5*x^2+5*x-1}; \end{axis} \end{tikzpicture}
